// VAULT MK7 - Browser Agent
const command = $input.first().json.command;
const sessionId = $input.first().json.session_id || 'default';

// Selenium Actions Database
const actions = {
  'google_search': (query) => `
    driver.get('https://www.google.com')
    search_box = driver.find_element(By.NAME, 'q')
    search_box.send_keys('${query}')
    search_box.send_keys(Keys.RETURN)
    time.sleep(2)
  `,
  'navigate': (url) => `driver.get('${url}')`,
  'click_element': (selector) => `driver.find_element(By.CSS_SELECTOR, '${selector}').click()`,
  'fill_form': (selector, text) => `
    element = driver.find_element(By.CSS_SELECTOR, '${selector}')
    element.clear()
    element.send_keys('${text}')
  `,
  'screenshot': () => `driver.save_screenshot('vault_screenshot.png')`
};

// Natural Language Processing
function parseCommand(cmd) {
  cmd = cmd.toLowerCase();

  if (cmd.includes('google') || cmd.includes('ara')) {
    const query = cmd.replace(/.*(?:google|ara)\s+/, '');
    return { action: 'google_search', params: query };
  }

  if (cmd.includes('git') || cmd.includes('website')) {
    const url = cmd.match(/https?:\/\/[^\s]+/) || ['https://www.google.com'];
    return { action: 'navigate', params: url[0] };
  }

  if (cmd.includes('screenshot') || cmd.includes('ekran')) {
    return { action: 'screenshot', params: null };
  }

  return { action: 'navigate', params: 'https://www.google.com' };
}

const parsed = parseCommand(command);
const seleniumCode = actions[parsed.action](parsed.params);

return {
  agent: 'BrowserAgent',
  command: command,
  selenium_code: seleniumCode,
  session_id: sessionId,
  timestamp: new Date().toISOString(),
  status: 'ready_to_execute'
};

