// VAULT MK7 - File Agent
const command = $input.first().json.command;
const sessionId = $input.first().json.session_id || 'default';

// File Operations Database
const fileOperations = {
  'create_file': (filename, content) => `
    with open('${filename}', 'w', encoding='utf-8') as f:
        f.write('${content}')
  `,
  'read_file': (filename) => `
    with open('${filename}', 'r', encoding='utf-8') as f:
        content = f.read()
    print(content)
  `,
  'delete_file': (filename) => `os.remove('${filename}')`,
  'copy_file': (source, dest) => `shutil.copy2('${source}', '${dest}')`,
  'move_file': (source, dest) => `shutil.move('${source}', '${dest}')`,
  'list_files': (directory) => `os.listdir('${directory || "."}')`
};

// Parse file commands
function parseFileCommand(cmd) {
  cmd = cmd.toLowerCase();

  if (cmd.includes('oluÅŸtur') || cmd.includes('create')) {
    const match = cmd.match(/([\w\-\.]+\.[\w]+)/);
    const filename = match ? match[1] : 'vault_file.txt';
    return { operation: 'create_file', params: [filename, 'VAULT MK7 Generated File'] };
  }

  if (cmd.includes('oku') || cmd.includes('read')) {
    const match = cmd.match(/([\w\-\.]+\.[\w]+)/);
    const filename = match ? match[1] : 'vault_file.txt';
    return { operation: 'read_file', params: [filename] };
  }

  if (cmd.includes('sil') || cmd.includes('delete')) {
    const match = cmd.match(/([\w\-\.]+\.[\w]+)/);
    const filename = match ? match[1] : 'vault_file.txt';
    return { operation: 'delete_file', params: [filename] };
  }

  if (cmd.includes('listele') || cmd.includes('list')) {
    return { operation: 'list_files', params: ['.'] };
  }

  return { operation: 'list_files', params: ['.'] };
}

const parsed = parseFileCommand(command);
const pythonCode = fileOperations[parsed.operation](...parsed.params);

return {
  agent: 'FileAgent',
  command: command,
  operation: parsed.operation,
  python_code: pythonCode,
  session_id: sessionId,
  timestamp: new Date().toISOString(),
  status: 'ready_to_execute'
};

// VAULT MK7 - Python Executor for Browser & File Operations
const inputData = $input.first().json;

// Create Python script based on agent type
let pythonScript = '';

if (inputData.agent === 'BrowserAgent') {
  pythonScript = `
# VAULT MK7 - Browser Agent Execution
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.common.keys import Keys
import time

try:
    driver = webdriver.Chrome()
    ${inputData.selenium_code}
    print(f"Browser command executed successfully: {inputData.command}")
except Exception as e:
    print(f"Browser execution error: {str(e)}")
finally:
    try:
        driver.quit()
    except:
        pass
  `;
} else if (inputData.agent === 'FileAgent') {
  pythonScript = `
# VAULT MK7 - File Agent Execution
import os
import shutil

try:
    ${inputData.python_code}
    print(f"File operation completed: {inputData.command}")
except Exception as e:
    print(f"File operation error: {str(e)}")
  `;
}

// Write Python script to temp file
const fs = require('fs');
const path = require('path');
const tempFile = path.join(process.cwd(), 'vault_temp_script.py');

fs.writeFileSync(tempFile, pythonScript);

return {
  agent: inputData.agent,
  command: inputData.command,
  script_path: tempFile,
  python_script: pythonScript,
  session_id: inputData.session_id,
  status: 'script_ready'
};

// VAULT MK7 - Response Coordinator
const allInputs = $input.all();
let responses = [];

allInputs.forEach(input => {
  const data = input.json;

  if (data.agent) {
    responses.push({
      agent: data.agent,
      command: data.command || data.prompt,
      result: data.stdout || data.candidates?.[0]?.content?.parts?.[0]?.text || data.result || 'Executed successfully',
      timestamp: new Date().toISOString(),
      session_id: data.session_id
    });
  }
});

// Combine all agent responses
const finalResponse = {
  vault_version: 'MK7',
  timestamp: new Date().toISOString(),
  session_id: responses[0]?.session_id || 'default',
  agents_executed: responses.length,
  responses: responses,
  summary: `VAULT MK7 executed ${responses.length} agent(s) successfully.`,
  status: 'completed'
};

return finalResponse;
