const input = $input.all()[0]?.json || {}
const command = input.command || ''
let responseMsg = ''
if (command.toLowerCase().includes('merhaba') || command.toLowerCase().includes('hello')) {
  responseMsg = 'Merhaba! Ben VAULT MK8. Size nasıl yardımcı olabilirim?';
} else if (command.toLowerCase().includes('yaz')) {
  responseMsg = 'İşte yazdığınız metin: ' + command;
} else {
  responseMsg = 'Komut alındı: ' + command;
}
return [{
  success: true,
  response: { message: responseMsg },
  command,
  agent: 'ai',
  status: 'completed',
  metadata: { version: 'mk8-simple', time: new Date().toISOString() }
}];
