  'list': (type) => ({ action: 'list', type: type || 'processes' }),
  'stop': (service) => ({ action: 'stop', target: service }),
  'execute': (cmd) => ({ action: 'execute', command: cmd, shell: true })
};

// Parse system command
function parseSystemCommand(cmd) {
  const words = cmd.toLowerCase().split(/\s+/);
  const action = words.find(word => Object.keys(systemCommands).includes(word));

  if (!action) {
    return {
      action: 'execute',
      command: cmd,
      shell: true,
      warning: 'Direct command execution - use with caution'
    };
  }

  const target = parameters.process || words.slice(words.indexOf(action) + 1).join(' ');
  return systemCommands[action](target);
}

